package com.rodemark.entities.field;import com.rodemark.base_entity.Entity;import com.rodemark.base_entity.Herbivore;import com.rodemark.base_entity.Predator;import java.util.*;public class WorldMap {    private int width = 10;    private int height = 10;    private final HashMap<Cell, Entity> entities;    public WorldMap(HashMap<Cell, Entity> entities) {        this.entities = entities;    }    public WorldMap(HashMap<Cell, Entity> entities, int height, int width) {        this.width = width;        this.height = height;        this.entities = entities;    }    public int getWidth() {        return width;    }    public int getHeight() {        return height;    }    public int getMapSize(){        return width * height;    }    public void addEntity(Entity entity){        entities.put(entity.getPosition(), entity);    }    public void removeEntity(Entity entity){        entities.remove(entity.getPosition());    }    public Entity getEntityFromCell(Cell cell){        return entities.get(cell);    }    public HashMap<Cell, Entity> getEntities() {        return entities;    }    public boolean existPredators(){        for (Map.Entry<Cell, Entity> entry : entities.entrySet()) {            Entity entity = entry.getValue();            if (entity instanceof Predator){                return true;            }        }        return false;    }    public boolean existHerbivores(){        for (Map.Entry<Cell, Entity> entry : entities.entrySet()) {            Entity entity = entry.getValue();            if (entity instanceof Herbivore){                return true;            }        }        return false;    }    public void removeAllPredators() {        if (existPredators()) {            Iterator<Map.Entry<Cell, Entity>> iterator = entities.entrySet().iterator();            while (iterator.hasNext()) {                Map.Entry<Cell, Entity> entry = iterator.next();                Entity entity = entry.getValue();                if (entity instanceof Predator) {                    iterator.remove();                }            }        }    }    public void printMap() {        String ANSI_BG_BROWN = "\u001B[43m";        String ANSI_BG_DEFAULT = "\u001B[0m";        StringBuilder stringBuilder = new StringBuilder();        stringBuilder.append("â€”".repeat(width * 3 + 3));        System.out.println(ANSI_BG_BROWN + stringBuilder);        for (int y = 0; y < height; y++) {            for (int x = 0; x < width; x++) {                if (x == 0) {                    System.out.print("  ");                }                Cell currentCell = new Cell(x, y);                Entity entity = entities.get(currentCell);                if (entity != null) {                    System.out.print(entity.getSymbol() + " ");                } else {                    System.out.print(". " + " ");                }            }            System.out.println();        }        System.out.println(stringBuilder);        System.out.println(ANSI_BG_DEFAULT);    }}